sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var overDueTasks = new GlideRecord('x_58872_needit_needit');

    overDueTasks.addEncodedQuery('due_dateBETWEENjavascript:gs.beginningOfToday()@javascript:gs.endOfTomorrow()');
    overDueTasks.addQuery('state', '<', 3);
    overDueTasks.addQuery('assigned_to', current.assigned_to);
    overDueTasks.query();
	gs.info(overDueTasks.getRowCount());

    if (overDueTasks.getRowCount() == 1) {
        template.print("You have " + overDueTasks.getRowCount() + " task due before the end of tommorow");
        template.print("\n");
        template.print("Please complete this ASAP!");

    } else if (overDueTasks.getRowCount() > 1) {
        template.print("You have " + overDueTasks.getRowCount() + " tasks due before the end of tommorow");
        template.print("\n");
        template.print("Please complete them ASAP!");

    } else {
        template.print("You have " + overDueTasks.getRowCount() + " tasks due before the end of tommorow?");
        template.print("\n");

        template.print("This email likely should not have been sent!!!");


    }



})(current, template, email, email_action, event);
