var numRecords = 15;

var users = ['04c63ac987713010672cedf73cbb3568','62826bf03710200044e0bfc8bcbe5df1','a8f98bb0eb32010045e1a5115206fe3a'];

function getRandomInt(max) {
  return Math.floor((Math.random() * max) + 1);
}


for (i = 0; i < numRecords; i++) {
	var rightNow = GlideDateTime();
	rightNow.addDaysUTC(getRandomInt(15));

	var newRecord = new GlideRecord('x_58872_needit_needit');

	var requestType = getRandomInt(3);
	var state = '' + getRandomInt(4);
	var priority = getRandomInt(5);
	var assignedTo = users[getRandomInt(3) - 1];
	var dueDate = rightNow;

	// add these values and the date maybe to newRecord
	newRecord.request_type = requestType;
	newRecord.state = state;
	newRecord.priority = priority;
	newRecord.assigned_to = assignedTo;
	newRecord.due_date = dueDate;


	newRecord.insert();


}
